import pytest
def longest_valid_paren(s):
    """
    longest valid parenthesis in a string only containing parenthesis
    """
    # forward
    i = 0
    local_list = []
    while i+1 < len(s):
        i = s.find('(', i)
        if i == -1:
            break

        l_vector = [1]
        j = 1
        cnt = 1
        local_z = 0
        while l_vector[j-1] != -1 and i+1 < len(s):
            l_vector.append(l_vector[j-1] + 1 if s[i+1] == '(' else l_vector[j-1] - 1)
            cnt += 1
            if l_vector[j] == 0:
                local_z = cnt
            i += 1
            j += 1
        local_list.append(local_z)

    # backward
    i = len(s)
    while i-1 > -1:
        i = s.rfind(')', 0, i)
        if i == -1:
            break

        l_vector = [1]
        j = 1
        cnt = 1
        local_z = 0
        while l_vector[j-1] != -1 and i-1 > -1:
            l_vector.append(l_vector[j-1] + 1 if s[i-1] == ')' else l_vector[j-1] - 1)
            cnt += 1
            if l_vector[j] == 0:
                local_z = cnt
            i -= 1
            j += 1
        local_list.append(local_z)

    return max(local_list, default=0)

